mapper.select:
select u.id,u.account,u.name,u.sex,u.age,u.createtime,u.updatetime,c.name collegeName,u.college_id collegeId from user u,college c where u.college_id = c.id
and u.name like '%${user.name}%'
and u.college_id = #{user.collegeId}
order by u.updatetime desc limit #{page},#{size}

select count(*) from user u


        u.college_id = #{user.collegeId}


        and u.name like '%${user.name}%'
